	    usr_zip_code as 'zip',
	    usr_phone as 'phone'
	FROM buy_buyqr  buy
    INNER JOIN usr_user usr ON buy.usr_id = usr.usr_id
    WHERE ((vRole=1 AND buy.usr_id = pUsrId) OR (vRole=2))
    ORDER BY buy.buy_id DESC;
	
END ;;
