			IF (vId >0) THEN
				INSERT INTO `cxu_clixusr`
				(
					`usr_id`,
					`cli_id`
				)
				VALUES
				(
					vUsrId,
					vId
				);
			END IF;
        END IF;
	ELSE
		SELECT IFNULL(cxu_id,0)
		INTO vCxuId
		FROM cxu_clixusr 
		WHERE usr_id = vUsrId AND cli_id = vId;
        
		IF (vCxuId = 0) THEN
			INSERT INTO `cxu_clixusr`
			(
				`usr_id`,
                `cli_id`
			)
			VALUES
			(
				vUsrId,
				vId
			);
		END IF;
	END IF;

	
	IF (vId = 0 OR vError>0) THEN
		ROLLBACK;
		SELECT 
			vError as 'code',
			vMsg as 'message';
	ELSE
		COMMIT;
		SELECT 
			vError as 'code',
			vMsg as 'message',
			vwL.*
		FROM vw_login_client vwL
		WHERE vwL.id = vId;
	END IF;
