CREATE DEFINER=`menu_user`@`localhost` PROCEDURE `SP_UPDATE_CLIENT`(
	IN pCode VARCHAR(8),
    IN pEmail VARCHAR(45),
	IN pName VARCHAR(100),
	IN pPhone VARCHAR(45),
    IN pAddress VARCHAR(100),
	IN pZip VARCHAR(5)
)
