CREATE DEFINER=`menu_user`@`localhost` PROCEDURE `SP_GET_MENU_SITE2`(
	IN pCode VARCHAR(8),
    IN pMenuId INT,
    IN pDelivery INT
)
BEGIN   

	DECLARE vUsrId INT DEFAULT 0;
    DECLARE vTimeNow INT;
    DECLARE vMultipleQR INT;
    DECLARE vMenId INT;
    DECLARE vCanDelivery INT;
    DECLARE vOwnStyle INT;
    
    DECLARE vName VARCHAR(45);
    DECLARE vAvatar VARCHAR(70);
    DECLARE vWebsite VARCHAR(255);
    DECLARE vCostShipping FLOAT;
    DECLARE vTimeS INT;
    DECLARE vTimeE INT;
    DECLARE vDelivery INT;
    DECLARE vType INT;
    DECLARE vCovid19 INT;
	DECLARE vCovid19Men INT;
    DECLARE vEnable INT;
    
    SELECT 
		usr.usr_id,
        IF(usr_multiple_qr,1,0),
        usr_company,
        usr_avatar,
        usr_website,
        usr_cost_shipping,
        usr_time_start_delivery,
        usr_time_end_delivery,
        CASE WHEN (usr_can_delivery) THEN 1 ELSE 0 END,
        (usr_type + 0),
        CASE WHEN (usr_covid19+0=3) THEN 1 ELSE 0 END
    INTO 
		vUsrId,
        vMultipleQR,
        vName,
        vAvatar,
        vWebsite,
        vCostShipping,
        vTimeS,
        vTimeE,
        vDelivery,
        vType,
        vCovid19
    FROM usr_user usr
    INNER JOIN cod_code_user cod ON cod.usr_id = usr.usr_id
    WHERE cod_code like pCode;
    
    SET vTimeNow = ((DATE_FORMAT(CONVERT_TZ(NOW(),'SYSTEM','-03:00'),'%H')*60)+DATE_FORMAT(CONVERT_TZ(NOW(),'SYSTEM','-03:00'),'%i'));
    IF (vMultipleQR=0 AND pMenuId>0) THEN
		SET pMenuId = 0;
	END IF;
    
    IF (pMenuId=0) THEN
		SELECT MAX(men_id)
        INTO vMenId
		FROM men_menu 
		WHERE usr_id = vUsrId AND vTimeNow BETWEEN men_time_start AND men_time_end
			AND SUBSTRING(men_days,DAYOFWEEK(NOW()),1)='1'
			AND men_enable = 1;
	ELSE
		SET vMenId = pMenuId;
	END IF;
    
	SELECT 
		CASE WHEN (men_can_delivery) THEN 1 ELSE 0 END,
        CASE WHEN (men_own_style) THEN 1 ELSE 0 END,
        CASE WHEN (men_enable) THEN 1 ELSE 0 END,
        CASE WHEN (vCovid19=1) THEN
			CASE WHEN (men_form_covid19) THEN
				vCovid19
			ELSE
				0
			END
        ELSE
			vCovid19
        END
	INTO 
		vCanDelivery,
        vOwnStyle,
        vEnable,
        vCovid19Men
	FROM men_menu 
	WHERE men_id=vMenId;
        
    IF (vUsrId>0) THEN
		/* Return header */
		SELECT 
			vName as 'name',
			vAvatar as 'avatar',
			vWebsite as 'website',
			vCostShipping as 'costShipping',
			vTimeS as 'timeS',
			vTimeE as 'timeE',
			CASE WHEN (vDelivery=1 AND vCanDelivery=1) THEN 1 ELSE 0 END as 'canDelivery',
			pCode as 'codeRestaurant',
            vMultipleQR as 'multipleQR',
            vType AS 'type',
            vOwnStyle as 'ownStyle',
            vMenId as 'menId',
            pMenuId as 'menIdCache',
            vCovid19Men as 'covid19',
            vEnable as 'menEnable';
    
		/* Return menu */
		SELECT *
		FROM _menu_site
		WHERE usrId = vUsrId 
        AND (menId = vMenId OR menId IS NULL)
		AND (pDelivery=0 OR (pDelivery=1 AND delivery=1))
		ORDER BY
			`orderDisplay` ASC,
			`order` ASC;
    END IF;
    
END ;;
