    AND ord.ord_created_at < DATE_SUB(NOW(), INTERVAL 0 HOUR)
    AND ord.ord_created_at > DATE_SUB(NOW(), INTERVAL 24 HOUR)
    ORDER BY ord_status ASC;
END ;;
