CREATE DEFINER=`menu_user`@`localhost` PROCEDURE `SP_SAVE_FORM86`(
	IN pCode VARCHAR(8),
    IN pGroup INT,
	IN pEmail VARCHAR(45),
	IN pName VARCHAR(100),
    IN pDni VARCHAR(10),
    IN pAddress VARCHAR(100),
	IN pQ1 INT,
	IN pQ2 INT,
	IN pQ3 INT,
	IN pQ4 INT,
	IN pQ5 INT,
	IN pQ6 INT
)
BEGIN   

    DECLARE vError INT DEFAULT 0;
    DECLARE vMsg VARCHAR(255);
    DECLARE vId INT DEFAULT 0;
    DECLARE vFormId INT DEFAULT 0;
	DECLARE vUsrId INT DEFAULT 0;
    DECLARE vCxuId INT DEFAULT 0;
	DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN
		GET CURRENT DIAGNOSTICS CONDITION 1 vMsg = MESSAGE_TEXT;
        SELECT 301 as 'code', vMsg as message, vId AS 'id';
		ROLLBACK;
    END;
    START TRANSACTION;
    
    SELECT 
		FN_GET_USER(pCode)
    INTO 
		vUsrId;
    
	SELECT IFNULL(cli_id,0)
    INTO vId
    FROM cli_client 
    WHERE cli_email = pEmail;    
    
	IF (vId=0) THEN 
		IF (pGroup=0 OR pName='' OR pDni='' OR pAddress='') THEN 
			SET vError=210;
        ELSE
			INSERT INTO cli_client (cli_email, cli_name, cli_phone, cli_address, cli_zip, cli_hash, cli_created_at, cli_enable, cli_dni) 
			VALUES (pEmail, pName, NULL, pAddress, NULL, MD5(CONCAT(pEmail,NOW())), NOW(), 1, pDni);
			SET vId = LAST_INSERT_ID();
			
			IF (vId >0) THEN
				INSERT INTO `cxu_clixusr`
				(
					`usr_id`,
					`cli_id`
				)
				VALUES
				(
					vUsrId,
					vId
				);
			END IF;
        END IF;
	ELSE
		UPDATE cli_client 
        SET 
			cli_name = pName,
            cli_address = pAddress, 
            cli_dni = pDni
		WHERE cli_id=vId;
        
		SELECT IFNULL(cxu_id,0)
		INTO vCxuId
		FROM cxu_clixusr 
		WHERE usr_id = vUsrId AND cli_id = vId;
        
		IF (vCxuId = 0) THEN
			INSERT INTO `cxu_clixusr`
			(
				`usr_id`,
                `cli_id`
			)
			VALUES
			(
				vUsrId,
				vId
			);
		END IF;
	END IF;
    
	SELECT IFNULL(frm_id,0)
    INTO vFormId
    FROM frm_form86 
    WHERE usr_id = vUsrId AND cli_id = vId 
		AND frm_created_at < DATE_SUB(NOW(), INTERVAL 0 HOUR)
		AND frm_created_at > DATE_SUB(NOW(), INTERVAL 24 HOUR);
    
    IF (vFormId=0) THEN 
		INSERT INTO frm_form86
		(
		`usr_id`,
		`cli_id`,
		`frm_group`,
		`frm_q1`,
		`frm_q2`,
		`frm_q3`,
		`frm_q4`,
		`frm_q5`,
		`frm_q6`,
		`frm_created_at`)
		VALUES
		(
		vUsrId,
        vId,
        pGroup,
        pQ1,
        pQ2,
        pQ3,
        pQ4,
        pQ5,
        pQ6,
        NOW());
	ELSE
		UPDATE frm_form86
		SET
		`frm_group` = pGroup,
		`frm_q1` = pQ1,
		`frm_q2` = pQ2,
		`frm_q3` = pQ3,
		`frm_q4` = pQ4,
		`frm_q5` = pQ5,
		`frm_q6` = pQ6,
		`frm_created_at` = NOW()
		WHERE frm_id=vFormId;
    END IF;
    
	IF (vId = 0 OR vError>0) THEN
		ROLLBACK;
		SELECT 
			vError as 'code',
			vMsg as 'message';
	ELSE
		COMMIT;
		SELECT 
			vError as 'code',
			vMsg as 'message';
	END IF;
END ;;
